import React from "react";
import "./projectStyleSheet.css"



function ProjectComponent(props){
    const [clicked, setClicked] = React.useState(0);
    return(

          <div
            className={clicked?" p-10 text-white w-80 rounded-lg  divide-y-2 ":" p-10 text-white w-80 rounded-lg my-10 mx-10 divide-y-2 "} 
            id = "shadow1" //custom css cause tailwind can't do it
            style={{backgroundColor:"#464141" }}
            onClick={(event)=>{
                    setClicked(1);
                    event.target.scrollIntoView();
                }
                }
            //add a second box shadow in with a unique color lol
          >
            <h1 className="text-xl m-1">{props.title}</h1>
            <p>{props.text}</p>
          </div>

    );
}

export default ProjectComponent;
