import gsap from "gsap";
import React, { useRef, useEffect, useState, useMemo } from "react";

function TextAnimation() {
  const timeline = useMemo(() => gsap.timeline({ paused: true }), []);
  const ref1 = useRef(0);
  const [play, setPlay] = useState(false);

  useEffect(() => {
    timeline.from(ref1.current, {
      y: 100,
      x: 100,
    });
  }, []);

  useEffect(() => {
    if (play) {
      timeline.play();
    } else {
      timeline.reverse();
    }
  }, [play]);

  return (
    <>
      <button onClick={() => setPlay(1)}> forward</button>
      <button onClick={() => setPlay(0)}> reverse</button>
      <p ref={ref1} className="sticky top-0 text-white font-bold ">
        Hi I'm Daniel
      </p>
    </>
  );
}

export default TextAnimation;
