import React from "react";
import "./projectStyleSheet.css"



function ProjectComponent(props){
    let clicked=0;
    function onClick(){
        clicked=1;
        console.log("heyy")
    }
    return(

          <div
            className={clicked?" p-10 text-white w-80 rounded-lg my-10 mx-10 divide-y-2 shadow-transform":" p-10 text-white w-80 rounded-lg my-10 mx-10 divide-y-2 shadow-transform"} 
            id = "shadow1" //custom css cause tailwind can't do it
            style={{backgroundColor:"#464141" }}
            onClick={onClick}
            //add a second box shadow in with a unique color lol
          >
            <h1 className="text-xl m-1">{props.title}</h1>
            <p>{props.text}</p>
          </div>

    );
}

export default ProjectComponent;
