import GuessingGame from "./GuessingGame";

function About() {
  return (
    <div style={{ backgroundColor: "whitesmoke" }}>
      <h1
        className="text-center m-4 font-size-lg pt-4 font-bold section-header text-xl"
        id="AboutMe"
        style={{ ScrollMarginTop: "75px" }}
      >
        About
      </h1>
      <div className="flex flex-row flex-wrap items-center justify-center gap-x-20 mx-auto">
        <GuessingGame></GuessingGame>
        <div className="w-full" style={{width:"400px"}}>
          <h1 className=" text-centertext-gray-600 mb-4">Mission </h1>
          <div className="italic">I believe function comes before form - this is why I chose a simple sans font. I wanted to balacnce aesthetics with useability</div>
            <div className="mt-6"> Here are two of my favorite parts of this website: The retractable navigation bar, and the about me quiz. To create the retractable navigation bar I add an event listener to the top level app component which calls a method when the user scrolls, causing a state change. That state change is used with the conditional ternary operator and tailwind css to pass a prop to the navigation bar component which changes the height. The about me quiz draws on a json file of questions, answers, and correct answers, and uses it to populate the quiz body. The quiz component has multiple state arrays, and when the user enters an answer it gets saved in the array, and is compared to the correct answers at the end to calculate the score.
            </div>
        </div>
      </div>
      <div>This part should be below the flex part</div>
    </div>
  );
}

export default About;
