import React from "react";
import qBank from "../assets/questions";

class GuessingGame extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            questionBank: qBank,
            score: 0,
            currentQuestion:0,
        }
    }
    render(){
    return (
        <div>
        <div>Question {this.state.currentQuestion}</div>
        <div>{this.state.questionBank[this.state.currentQuestion].question}</div>
        <div>{this.state.questionBank[this.state.currentQuestion].answers}</div>
        <button> Previous </button>
        <button onClick = {()=>{this.setState(currentQuestion = currentQuestion+1)}}> Next</button>
        </div>
    );
    }
}

export default GuessingGame;
