import gsap from "gsap";
import React, { useRef, useEffect, useState, useMemo } from "react";

function TextAnimation() {
  const timeline = useMemo(() => gsap.timeline({ paused: true }), []);
  const [ref1, ref2, ref3, ref4, ref5] = [useRef(0), useRef(0), useRef(0), useRef(0), useRef(0) ]; //rewrite this to be cleaer if possible

  const [play, setPlay] = useState(true);

  useEffect(() => {
    timeline.to(ref2.current, {
      opacity:1,
    })
    .to(ref1.current, {
      y:"-=30", 
    })
     .to(ref3.current, {
      opacity:1,
    })
    .to(ref1.current, {
      y:"-=30", 
    })
    .to(ref4.current, {
      opacity:1,
    })
    .to(ref1.current, {
      y:"-=30", 
    })
  }, []);

  useEffect(() => {
    if (play) {
      timeline.play();
    } else {
      timeline.reverse();
    }
    console.log(play);
  }, [play]);

  return (
    <>
      <button onClick={() => setPlay(true)}> forward</button>
      <button onClick={() => setPlay(false)}> reverse</button>
      <div ref={ref1} className="text-white font-bold z-0">
        <p ref={ref2} className="opacity-0">Hi I'm Daniel</p>
        <p ref={ref3} className="opacity-0">Hi I'm Daniel</p>
        <p ref={ref4} className="opacity-0">Hi I'm Daniel</p>
        <p ref={ref5} className="opacity-0">Hi I'm Daniel</p>
      </div>
    </>
  );
}

export default TextAnimation;
